/*global google*/
import {
  Box,
  Button,
  ButtonGroup,
  Flex,
  HStack,
  IconButton,
  Input,
  SkeletonText,
  Text,
} from "@chakra-ui/react";
import { FaLocationArrow, FaTimes } from "react-icons/fa";
import React, { Component, useEffect } from "react";
import axios from "axios";

import {
  useJsApiLoader,
  GoogleMap,
  Marker,
  Autocomplete,
  DirectionsRenderer,
  InfoWindow,
} from "@react-google-maps/api";
import { useRef, useState } from "react";
import Geocode from "react-geocode";

Geocode.setApiKey("AIzaSyB4703oaX3tg6VcgRVVot_6WPs_-E-auco");
Geocode.enableDebug();

function App() {
  const [caraddress, setcaraddress] = useState("Loading ...");
  const [flag, setflag] = useState(0);
  const [getflag, setgetflag] = useState(0);
  const [center2, setcenter2] = useState({});
  const [center, setcenter] = useState({ lat: 12.9492, lng: 80.2547 });
  const [carDetails, setcarDetails] = useState([]);
  const [waypointsdata, setwaypointsdata] = useState([]);
  const [originValue, setoriginValue] = useState("");
  const [destinationValue, setdestinationValue] = useState("");
  const [polylineValue, setpolylineValue] = useState(0);
  const [pathValue, setPathValue] = useState(null);

  useEffect(() => {
    if (flag == 0) {
      navigator.geolocation.getCurrentPosition(function (position) {
        //console.log("Latitude is :", position.coords.latitude);
        //console.log("Longitude is :", position.coords.longitude);
        setcenter({
          lat: position.coords.latitude,
          lng: position.coords.longitude,
        });
      });
      setflag(1);
    }

    if (getflag > 0) {

    setTimeout(() => {
    	//console.log("Teting ++++++++" + getflag)
    	getTopicData()
    	setgetflag(getflag + 1)

    }, 5000)

    }
  }, [flag, getflag]);

  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: "AIzaSyB4703oaX3tg6VcgRVVot_6WPs_-E-auco",
    libraries: ["places"],
  });

  const [map, setMap] = useState(/** @type google.maps.Map */ (null));
  const [directionsResponse, setDirectionsResponse] = useState(null);
  const [topic_name, settopic_name] = useState("");
  const [car_alert, setcar_alert] = useState(0);

  /** @type React.MutableRefObject<HTMLInputElement> */
  const originRef = useRef();
  /** @type React.MutableRefObject<HTMLInputElement> */
  const destiantionRef = useRef();

  if (!isLoaded) {
    return <SkeletonText />;
  }

  async function getTopicData() {
    const getStocksData = {
      url: "http://localhost:5000/publish",
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      data: JSON.stringify({
        topic: topic_name,
      }),
    };
    axios(getStocksData)
      .then((response) => {
        //console.log("+++++++++++++++");
        //console.log(response.data);
        //console.log("+++++++++++++++");
        // setcarDetails(response.data.data)
        try {

        	if (response.data.data.speed >= response.data.threshold) {
        		// alert("Correct" + response.data.data.speed)
        		setcar_alert(1)
        	}
        	else {
        		// alert("error" + response.data.data.speed)
        		setcar_alert(0)

        	}
        }

        catch {
        	setcar_alert(0)

        	// alert("catch" + response.data.data.speed)

        }

        let new_location = {
        	"lat": response.data.data.lat,
        	"lng": response.data.data.lon

        }
        setcenter(new_location)
        setcenter2(new_location)
        Geocode.fromLatLng(response.data.data.lat, response.data.data.lon).then(
        	response => {
        		const address = response.results[0].formatted_address

        		//console.log(address)
        		setcaraddress(address)

        	},
        	error => {
        		//console.error(error);
        	}
        );
      })
      .catch(function (e) {
        //console.log(e.message);
        if (e.message === "Network Error") {
          alert("No Internet Found. Please check your internet connection");
        } else {
          alert(
            "Sorry, something went wrong. Please try again after sometime. If the issue still persists contact support."
          );
        }
      });
  }

  async function calculateRoute() {
    if (
      originRef.current.value === "" ||
      destiantionRef.current.value === "" ||
      topic_name === ""
    ) {
      alert("Please Enter Required Details!");
    }

    // eslint-disable-next-line no-undef
    const directionsService = new google.maps.DirectionsService();
    const results = await directionsService.route({
      origin: originRef.current.value,
      destination: destiantionRef.current.value,
      // eslint-disable-next-line no-undef
      travelMode: google.maps.TravelMode.DRIVING,
      waypoints: waypointsdata,
    });
    setDirectionsResponse(results);
    console.log("Resultssssss 1: ",results)

    setflag(flag + 1);
    setgetflag(getflag + 1);
  }

  async function calculateRouteNew() {
    // eslint-disable-next-line no-undef
    const directionsService = new google.maps.DirectionsService();
    const results = await directionsService.route({
      origin: originValue,
      destination: destinationValue,
      // eslint-disable-next-line no-undef
      travelMode: google.maps.TravelMode.DRIVING,
      waypoints: waypointsdata,
    });
    setDirectionsResponse(results);
    console.log("Resultssssss 2: ",results)

    setflag(flag + 1);
    setgetflag(getflag + 1);
  }

  function clearRoute() {
    setDirectionsResponse(null);

    originRef.current.value = "";
    destiantionRef.current.value = "";
  }

  function createPolyline(response) {
    var polyline = new google.maps.Polyline({
      path: [],
      strokeColor: '#FF0000',
      strokeWeight: 3
    });
    var bounds = new google.maps.LatLngBounds();
    var legs = response.routes[0].legs;
    for (var i=0;i<legs.length;i++) {
      var steps = legs[i].steps;
      for (var j=0;j<steps.length;j++) {
        var nextSegment = steps[j].path;
        for (var k=0;k<nextSegment.length;k++) {
          polyline.getPath().push(nextSegment[k]);
          bounds.extend(nextSegment[k]);
        }
      }
    }

    polyline.setMap(map);
    map.fitBounds(bounds);
  }

  return (
